import pytestfrom particle_in_field import EMWave, ChargedParticle, ParticleDiagnosticfrom particle_in_field import FieldPlottingDiagnostic, ForwardEulertest_input_data = {"Grid": {"N": 10, "min": 0, "max": 1},                   "Clock": {"start_time": 0, "end_time": 10,                             "num_steps": 4, "print_time": False},                   "PhysicsModules":                   {                   "PhysicsModules.EMWave": {"amplitude": 1, "omega": 1}},                   "PhysicsModules.ChargedParticle":                   {"position": 1, "Pusher": "ForwardEuler"}                   }@pytest.fixture(name="EM_example")def fixture_example():    return EMWave("PhysicsModules.EMWave", test_input_data)def test_EMWave(EM_example):    assert isinstance(EM_example, EMWave)@pytest.fixture(name="ChargedParticle_example")def fixture_example2():    return ChargedParticle("PhysicsModules.ChargedParticle", test_input_data)def test_ChargedParticle(ChargedParticle_example):    assert isinstance(ChargedParticle_example, ChargedParticle)